'use strict';
var moment = require( 'moment' );

function logMessage ( message, alertClass){
	var now = moment().format('YYYY-MM-DD HH:mm:ss');
	global.CONSOLE_OUTPUT.unshift({
		message : message,
		alert_class : alertClass,
		now : now
	});
	if(global.CONSOLE_OUTPUT.length > 10){
		global.CONSOLE_OUTPUT.pop();
	}
}

exports.logSuccess = function( message ){
	return logMessage( message,'alert-success' );
};

exports.logFailure = function( message ){
	return logMessage( message,'alert-danger' );
};

exports.logTitle = function( message ){
	return logMessage( message,'alert-info' );
};